def add(x,y):
    return x + y
def sub (x,y):
    return x - y
def mult (x,y):
    return x * y
def divi (x,y):
    return x / y

def add3(x,y,m):
    return x + y + m
def sub3 (x,y,m):
    return x - y - m
def mult3 (x,y,m):
    return x * y * m
def divi3 (x,y,m):
    return x / y / m

def add4(x,y,m,j):
    return x + y + m + j
def sub4 (x,y,m,j):
    return x - y - m - j
def mult4 (x,y,m,j):
    return x * y * m * j
def divi4 (x,y,m,j):
    return x / y / m / j