const today=new Date;function go_back(){window.history.back()}function go_forward(){window.history.forward()}function refresh(){window.location.reload()}function getKeyValue(val){let display=document.getElementById("display");display.value+=val}function addNumber(element,displayID){const display=document.getElementById(displayID);display.classList.add("keypad-display");display.value=display.value+element.value}function currentDate(){const year=today.getFullYear();const month=today.toLocaleString("default",{month:"long"});const day=today.toLocaleString("default",{day:"2-digit"});document.getElementById("date").innerText=month+" "+day+" "+year}function currentTime(){function updateTime(time){if(time<10){return"0"+time}else{return time}}let hour=updateTime(today.getHours());let min=updateTime(today.getMinutes());let mid_day=hour>=12?"PM":"AM";hour=hour===0?12:hour>12?hour-12:hour;document.getElementById("time").innerText=hour+":"+min+" "+mid_day;const t=setTimeout(function(){currentTime()},1e3)}function clearMessage(){setTimeout(function(){document.getElementById("message").remove()},3e3)}function reloadPage(){var currentDocumentTimestamp=new Date(performance.timing.domLoading).getTime();var now=Date.now();var tenSec=10*1e3;var plusTenSec=currentDocumentTimestamp+tenSec;if(now>plusTenSec){location.reload()}else{}}function reloadCssFiles(){var queryString="?reload="+(new Date).getTime();$('link[rel="stylesheet"]').each(function(){this.href=this.href.replace(/\?.*|$/,queryString)})}document.addEventListener("DOMContentLoaded",function(){M.AutoInit();const userSideNav=document.querySelectorAll("#user-side-nav.sidenav");const userSideNavInstance=M.Sidenav.init(userSideNav,{edge:"right"});const clickFABToolbar=document.querySelectorAll(".fixed-action-btn.toolbar");const clickFABToolbarInstance=M.FloatingActionButton.init(clickFABToolbar,{hoverEnabled:false,toolbarEnabled:true});currentTime();currentDate();clearMessage()});